	public int addPropertyDetail(propertyDetailModel propertyModel, String tokenNo, Date tokenDate, String dsr_location,
			int empID, Date currentDate) throws Exception {
		Session session = HibernateUtil.getSessionFactory().getCurrentSession();
		secondTokenTypeModel secondModel = new secondTokenTypeModel();
		int returnValue = 0;
		try {
			if (session != null) {
				session.beginTransaction();
				session.save(propertyModel);
				secondTokenPKModel secondTokenPK = new secondTokenPKModel();
				secondTokenPK.setToken_no(tokenNo);
				secondTokenPK.setToken_date(tokenDate);
				secondTokenPK.setDsr_location(Integer.parseInt(dsr_location));

				Criteria criteria = session.createCriteria(secondTokenTypeModel.class)
						.add(Restrictions.eq("secondTokenPK", secondTokenPK));

				secondModel = (secondTokenTypeModel) criteria.uniqueResult();

				if ((secondModel.getToken_status().trim().equalsIgnoreCase("global_p"))
						|| (secondModel.getToken_status().trim().equalsIgnoreCase("global_dd"))) {

					if (secondModel.getToken_status().trim().equalsIgnoreCase("global_dd")) {

						String hql1 = "delete from  deedDetailModel " + "WHERE deedDetailPK =:deedDetailPK";
						Query query1 = session.createQuery(hql1);

						deedDetailPKModel deedDetailPK = new deedDetailPKModel();
						deedDetailPK.setToken_no(tokenNo);
						deedDetailPK.setToken_date(tokenDate);
						deedDetailPK.setDsr_location(Integer.parseInt(dsr_location));

						query1.setParameter("deedDetailPK", deedDetailPK);
						query1.executeUpdate();

					}

					String hql = "UPDATE secondTokenTypeModel set token_status =:token_status,updated_date= :currentDate, updated_by= :empID "
							+ "WHERE secondTokenPK =:secondTokenPK";
					Query query = session.createQuery(hql);

					secondTokenPK.setToken_no(tokenNo);
					secondTokenPK.setToken_date(tokenDate);
					secondTokenPK.setDsr_location(Integer.parseInt(dsr_location));

					if (secondModel.getToken_status().trim().equalsIgnoreCase("global_p"))
						query.setParameter("token_status", "global_pd");
					else if (secondModel.getToken_status().trim().equalsIgnoreCase("global_dd"))
						query.setParameter("token_status", "global_pt");

					query.setParameter("secondTokenPK", secondTokenPK);
					query.setParameter("currentDate", currentDate);
					query.setParameter("empID", empID);

					query.executeUpdate();
				}
				session.getTransaction().commit();
				returnValue = 1;
			} else
				returnValue = 0;

		} catch (Exception e) {
			session.getTransaction().rollback();
			returnValue = 0;
			log.fatal(helper.writeLogInUpperCase(e));
			throw new Exception("System Error Occured");

		} finally {
			if (session.isOpen())
				session.close();
		}
		return returnValue;

	}

