	public int addFirstToken(provisionalTokenTypeModel tokenModel) throws Exception {
		log.fatal("addFirstToken...Inserting");
		Session session = HibernateUtil.getSessionFactory().getCurrentSession();
		int returnValue = 0;
		try {
			if (session != null) {
				session.beginTransaction();
				session.save(tokenModel);
				session.getTransaction().commit();
				returnValue = 1;
			} else
				returnValue = 0;

		} catch (Exception e) {
			e.printStackTrace();
			returnValue = 0;
			session.getTransaction().rollback();
			log.fatal(helper.writeLogInUpperCase(e));
			throw new Exception("System Error Occured");

		} finally {
			if (session.isOpen())
				session.close();
		}
		return returnValue;

	}

	public int searchPageNo(String token_no, Date token_date, int dsr_location) throws Exception {
		Session session = HibernateUtil.getSessionFactory().getCurrentSession();

		int page_no = 0;

		try {
			if (session != null) {
				session.beginTransaction();

				Query query = session.createQuery("select cast(original_deed_total_page_no as int)  from provisionalTokenTypeModel E  where   E.token_no =:token_no");
				// and E.token_date =:token_date   and E.dsr_location =:dsr_location

				query.setParameter("token_no", token_no);//String.valueOf(token_no)
				//query.setParameter("token_date", token_date);
				//query.setParameter("dsr_location", dsr_location);

				page_no = (int) query.uniqueResult();

			}

		} catch (Exception e) {
			log.fatal(helper.writeLogInUpperCase(e));
			throw new Exception("System Error Occured");

		} finally {
			if (session.isOpen())
				session.close();
		}

		log.fatal("return page_no" + page_no);
		return page_no;

	}

