	public String getValuation(float areaVolume, int propertyType, int district_id, String area_unit) throws Exception {
		Session session = HibernateUtil.getSessionFactory().getCurrentSession();
		BigDecimal propertyTypeRate = new BigDecimal(0);
		String returnString = "0.0";
		try {
			if (session != null) {
				session.beginTransaction();
				Query q = session
						.createQuery(" SELECT property_type_val FROM propertyTypeModel E WHERE    property_type_id="
								+ propertyType + " and is_active=true and district_id=" + district_id);
				propertyTypeRate = (BigDecimal) q.uniqueResult();

				if (area_unit.equalsIgnoreCase("global_area_unit_hector")) {
					areaVolume = areaVolume * 10120f;
				}

				else if (area_unit.equalsIgnoreCase("global_area_unit_sqf")) {
					areaVolume = areaVolume * 0.092902f;
				} else if (area_unit.equalsIgnoreCase("global_area_unit_bigha")) {
					areaVolume = areaVolume * 1396.8f;
				} else if (area_unit.equalsIgnoreCase("global_area_unit_katta")) {
					areaVolume = areaVolume * 69.84f;
				} else if (area_unit.equalsIgnoreCase("global_area_unit_acre")) {
					areaVolume = areaVolume * 3900f;
				} else if (area_unit.equalsIgnoreCase("global_area_unit_sqm")) {
					areaVolume = areaVolume;
				}

				returnString = String.valueOf(propertyTypeRate.floatValue() * areaVolume);
				BigDecimal value = new BigDecimal(returnString);
				returnString = String.valueOf(value.setScale(2, BigDecimal.ROUND_HALF_UP));
			}

		} catch (Exception e) {
			log.fatal(helper.writeLogInUpperCase(e));
			throw new Exception("System Error Occured");

		} finally {
			if (session!=null && session.isOpen())
				session.close();
		}
		return returnString;

	}

