			JasperPrintManager printManager = new JasperPrintManager();
			BufferedImage rendered_image = null;
			PDXObjectImage ximage = null;
			PDFMergerUtility merger = new PDFMergerUtility();
			long currentMiliSecond = System.currentTimeMillis();
			String fileLocation = "/home/jboss/portal/download/";
			/*String fileLocation = "D://";*/
			String reportName = "IGR_CHECKSLIP";
			
			log.fatal("GenerateReport fileLocation="+fileLocation);

			File file = new File(fileLocation + reportName + "_" + token_no + "~" + sdf1.format(sdf.parse(token_date))
					+ "~" + dsr_location + "~" + currentMiliSecond + ".gif");

			for (int i = 0; i < jasperPrint.getPages().size(); i++) {
				rendered_image = (BufferedImage) printManager.printPageToImage(jasperPrint, i, 1);

				/*
				 * ouputStream = new FileOutputStream(file);
				 * 
				 * ImageIO.write(rendered_image, "gif", ouputStream);
				 */

				PDDocument doc = new PDDocument();
				PDPage page = new PDPage();
				page.setMediaBox(PDPage.PAGE_SIZE_A4);
				// page.setRotation(90);
				doc.addPage(page);

				/*
				 * BufferedImage awtImage = ImageIO.read(new File(fileLocation +
				 * reportName + "_" + token_no + "~" +
				 * sdf1.format(sdf.parse(token_date)) + "~" + dsr_location + "~"
				 * + currentMiliSecond + ".gif"));
				 */

				ximage = new PDPixelMap(doc, rendered_image);

				PDPageContentStream contentStream = new PDPageContentStream(doc, page);

				Dimension scaledDim = getScaledDimension(new Dimension(ximage.getWidth(), ximage.getHeight()),
						page.getMediaBox().createDimension());

				contentStream.drawXObject(ximage, 1, 1, scaledDim.width, scaledDim.height);

				contentStream.close();

				doc.save(fileLocation + reportName + "_" + token_no + "~" + sdf1.format(sdf.parse(token_date)) + "~"
						+ dsr_location + "~" + currentMiliSecond + "_" + i + ".pdf");

				merger.addSource(fileLocation + reportName + "_" + token_no + "~" + sdf1.format(sdf.parse(token_date))
						+ "~" + dsr_location + "~" + currentMiliSecond + "_" + i + ".pdf");
				doc.close();

			}

			merger.setDestinationFileName(fileLocation + reportName + "_" + token_no + "~"
					+ sdf1.format(sdf.parse(token_date)) + "~" + dsr_location + "~" + currentMiliSecond + ".pdf");

			merger.mergeDocuments();

			PDDocument imageDoc = PDDocument.load(fileLocation + reportName + "_" + token_no + "~"
					+ sdf1.format(sdf.parse(token_date)) + "~" + dsr_location + "~" + currentMiliSecond + ".pdf");

			pageCount = jasperPrint.getPages().size();

			File file1 = new File(fileLocation);
			File[] files1 = file1.listFiles();
			for (File f : files1) {
				if (f.getName().startsWith(reportName + "_" + token_no + "~" + sdf1.format(sdf.parse(token_date)) + "~"
						+ dsr_location + "~" + currentMiliSecond))
					f.delete();
			}

